import React, { useState } from 'react';
import data from '../data'; // Import the data from the JSON file

export default function Home() {
  const [price, setPrice] = useState(0);

  const handleSliderChange = (event) => {
    setPrice(event.target.value);
  };

  return (
    <div>
      {/* Carousel Section */}
      <div id="responsiveSlider" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-indicators">
          {data.carousel.indicators.map((indicator, index) => (
            <button
              key={index}
              type="button"
              data-bs-target={indicator.target}
              data-bs-slide-to={indicator.slideTo}
              className={index === 0 ? 'active' : ''}
              aria-current={index === 0 ? 'true' : 'false'}
              aria-label={indicator.ariaLabel}
            ></button>
          ))}
        </div>

        <div className="carousel-inner">
          {data.carousel.items.map((item, index) => (
            <div key={index} className={`carousel-item ${index === 0 ? 'active' : ''}`}>
              <img src={item.imageSrc} className="w-100" alt={item.alt} />
              <div className="slide-name">{item.caption}</div>
            </div>
          ))}
        </div>

        <button className="carousel-control-prev" type="button" data-bs-target="#responsiveSlider" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#responsiveSlider" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>

      {/* Filters Section */}
      <div className="col-lg-3 mb-4 filters">
        <h5>Filters</h5>
        <div className="mb-3">
          <h6>Storage</h6>
          {data.filters.storageOptions.map((option, index) => (
            <div key={index}>
              <input type="checkbox" /> {option}
            </div>
          ))}
        </div>

        {/* Budget Range Slider */}
        <div className="mb-3">
          <h6>Budget</h6>
          <input
            type="range"
            className="form-range"
            min={data.filters.budget.min}
            max={data.filters.budget.max}
            step={data.filters.budget.step}
            value={price}
            onChange={handleSliderChange}
          />
          <div>
            <strong>Selected Budget: ₹{price}</strong>
          </div>
        </div>

        {/* Technology and Water Source Filters */}
        <div className="mb-3">
          <h6>Technology</h6>
          {data.filters.technologies.map((tech, index) => (
            <div key={index}>
              <input type="checkbox" /> {tech}
            </div>
          ))}
        </div>

        <div className="mb-3">
          <h6>Source of Water</h6>
          {data.filters.waterSource.map((source, index) => (
            <div key={index}>
              <input type="checkbox" /> {source}
            </div>
          ))}
        </div>
      </div>

      {/* Products Section */}
      <div className="col-lg-9">
        <div className="row">
          {data.products.map((product, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="product-card p-3">
                <img src={product.image} alt={product.name} />
                <h6 className="mt-3">{product.name}</h6>
                <p className="text-muted">{product.price}</p>
                <button className="btn btn-primary w-100">Learn More</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
