// Import necessary libraries
import React, { useState, useEffect } from 'react';
import data from '../home.json'; // Ensure this file exists and has valid JSON structure

// Carousel Component
function Carousel({ items = [] }) {
  // Check if items is an array and has content
  if (!Array.isArray(items) || items.length === 0) {
    return <p>Loading carousel...</p>;
  }
  return (
    <div id="responsiveSlider" className="carousel slide" data-bs-ride="carousel">
      <div className="carousel-indicators">
        {items.map((item, index) => (
          <button
            key={index}
            type="button"
            data-bs-target="#responsiveSlider"
            data-bs-slide-to={index}
            className={index === 0 ? "active" : ""}
            aria-current={index === 0 ? "true" : "false"}
            aria-label={item.caption}
          ></button>
        ))}
      </div>
      <div className="carousel-inner">
        {items.map((item, index) => (
          <div className={`carousel-item ${index === 0 ? 'active' : ''}`} key={index}>
            <img src={item.image} className="w-100" alt={item.altText} />
            <div className="slide-name">{item.caption}</div>
          </div>
        ))}
      </div>
      <button className="carousel-control-prev" type="button" data-bs-target="#responsiveSlider" data-bs-slide="prev">
        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Previous</span>
      </button>
      <button className="carousel-control-next" type="button" data-bs-target="#responsiveSlider" data-bs-slide="next">
        <span className="carousel-control-next-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Next</span>
      </button>
    </div>
  );
}

// Filters Component
function Filters({ filters = {}, price, handleSliderChange }) {
  if (!filters || typeof filters !== 'object') {
    return <p>Loading filters...</p>;
  }
  return (
    <div className="col-lg-3 mb-4 filters">
      <h5 style={{ color: "#00254f" }}>Filters</h5>
      <div className="mb-3">
        <h6>Storage</h6>
        {filters.storage?.map((storage, index) => (
          <div key={index} style={{ color: "#00254f" }}>
            <input type="checkbox" /> {storage}
          </div>
        ))}
      </div>
      <div className="mb-3" style={{ color: "#00254f" }}>
        <h6>Budget</h6>
        <input
          type="range"
          className="form-range"
          min={filters.budget?.min || 0}
          max={filters.budget?.max || 10000}
          step={filters.budget?.step || 500}
          value={price}
          onChange={handleSliderChange}
        />
        <div><strong>Selected Budget: ₹{price}</strong></div>
      </div>
      <div className="mb-3">
        <h6>Technology</h6>
        {filters.technology?.map((tech, index) => (
          <div key={index} style={{ color: "#00254f" }}>
            <input type="checkbox" /> {tech}
          </div>
        ))}
      </div>
      <div className="mb-3">
        <h6>Source of Water</h6>
        {filters.sourceOfWater?.map((source, index) => (
          <div key={index} style={{ color: "#00254f" }}>
            <input type="checkbox" /> {source}
          </div>
        ))}
      </div>
    </div>
  );
}

// ProductCard Component
function ProductCard({ product }) {
  if (!product) return null;
  return (
    <div className="col-md-4 mb-4">
      <div className="product-card p-3">
        <img src={product.image} alt={product.name} />
        <h6 className="mt-3" style={{ color: "#00254f" }}>{product.name}</h6>
        <p className="text-muted" style={{ color: "#00254f" }}>{product.price}</p>
        <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
      </div>
    </div>
  );
}

// Home Component
export default function Home() {
  const [price, setPrice] = useState(0);
  const [carouselItems, setCarouselItems] = useState([]);
  const [filters, setFilters] = useState({});
  const [products, setProducts] = useState([]);

  useEffect(() => {
    if (data) {
      setCarouselItems(data.carousel || []);
      setFilters(data.filters || {});
      setProducts(data.products || []);
    }
  }, []);

  const handleSliderChange = (event) => {
    setPrice(event.target.value);
  };

  return (
    <div>
      <Carousel items={carouselItems} />
      <div className="container my-4">
        <div className="text-center mb-4">
          <h1 style={{ color: "#00254f" }}>Pureit Water Purifier</h1>
          <p style={{ color: "#00254f" }}>Choose a Water Purifier that best suits your needs & budget.</p>
        </div>
        <div className="row">
          <Filters filters={filters} price={price} handleSliderChange={handleSliderChange} />
          <div className="col-lg-9">
            <div className="row">
              {products.map((product, index) => (
                <ProductCard key={index} product={product} />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
