import React, { useState } from 'react';

export default function Home() {
  // State to manage the budget range
  const [budget, setBudget] = useState(500);

  // Function to handle slider change
  const handleBudgetChange = (event) => {
    setBudget(event.target.value);
  };

  return (
    <div>
      {/* Slider Section */}
      <div className="container my-4">
        <div className="text-center mb-4">
          <h1 style={{ color: "#00254f" }}>Pureit Water Purifier</h1>
          <p style={{ color: "#00254f" }}>
            Choose a Water Purifier that best suits your needs & budget.
          </p>
        </div>

        <div className="row">
          <div className="col-md-3">
            {/* Budget Range Filter */}
            <div className="filters mb-4">
              <h5>Filters</h5>
              <div className="mb-3">
                <label className="form-label">Budget (₹500 to ₹1000)</label>
                <input
                  type="range"
                  className="form-range"
                  min="500"
                  max="1000"
                  step="100"
                  value={budget}
                  onChange={handleBudgetChange}
                />
                <p>
                  Selected Budget: <b>₹{budget}</b>
                </p>
              </div>
            </div>
          </div>

          {/* Product Listing Section */}
          <div className="col-lg-9">
            {/* Sort Options */}
            <div className="d-flex justify-content-between align-items-center mb-4">
              <div style={{ color: "#00254f" }}>
                <input type="checkbox" /> Recommendations based on Pin Code
              </div>
              <select className="form-select w-auto">
                <option>Price: High to Low</option>
                <option>Price: Low to High</option>
              </select>
            </div>

            {/* Products Grid */}
            <div className="row">
              {/* Product Example */}
              <div className="col-md-4 mb-4">
                <div className="product-card p-3">
                  <img
                    src="https://via.placeholder.com/300x300"
                    alt="Product 1"
                    className="img-fluid"
                  />
                  <h6 className="mt-3" style={{ color: "#00254f" }}>
                    Pureit Copper+ Mineral RO+UV Water Purifier
                  </h6>
                  <p className="text-muted">₹{budget}</p>
                  <button className="btn btn-primary w-100">Learn More</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}





