              <div className="mb-3">
                <label className="form-label">Storage</label>
                <div>
                  <input type="checkbox" id="small" /> Small <br />
                  <input type="checkbox" id="medium" /> Medium <br />
                  <input type="checkbox" id="large" /> Large
