          <p>Selected Budget: <b>₹0</b></p>
        </div>
        <div className="mb-3">
          <label className="form-label">Technology</label>
          <div>
            <input type="checkbox" id="ro" /> RO <br />
            <input type="checkbox" id="uv" /> UV <br />
            <input type="checkbox" id="copper" /> Copper Range
          </div>
        </div>
        <div className="mb-3">
          <label className="form-label">Source of Water</label>
          <div>
            <input type="checkbox" id="hard" /> Hard Water <br />
            <input type="checkbox" id="soft" /> Soft Water
          </div>
        </div>
      </div>

      {/* <!-- Video Container --> */}
