                <input type="checkbox" /> Recommendations based on Pin Code
              </div>
              <select className="form-select w-auto">
                <option style={{ color: "#00254f" }}>Price: High to Low</option>
                <option style={{ color: "#00254f" }}>Price: Low to High</option>
              </select>
            </div>

            {/* <!-- Products Grid --> */}
            <div className="row">
              <div className="col-md-4 mb-4">
                <div className="product-card p-3">
                  <img src="https://tse4.mm.bing.net/th?id=OIP._k4ZlNcllPMJxvPwekpTcwHaHa&pid=Api&P=0&h=180" alt="Product 1" />
                  <h6 className="mt-3" style={{ color: "#00254f" }}>Pureit Copper+ Mineral RO+UV Water Purifier</h6>
                  <p className="text-muted" style={{ color: "#00254f" }}>₹15,000</p>
                  <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
                </div>
              </div>
              <div className="col-md-4 mb-4">
                <div className="product-card p-3">
                  <img src="https://via.placeholder.com/300x300" alt="Product 2" />
                  <h6 className="mt-3" style={{ color: "#00254f" }}>Pureit Ultima Mineral RO+UV Water Purifier</h6>
                  <p className="text-muted" style={{ color: "#00254f" }}>₹18,000</p>
                  <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
                </div>
              </div>
              <div className="col-md-4 mb-4">
                <div className="product-card p-3">
                  <img src="https://via.placeholder.com/300x300" alt="Product 3" />
                  <h6 className="mt-3" style={{ color: "#00254f" }}>Pureit Eco RO+MF Water  Water   Water Purifier</h6>
                  <p className="text-muted" style={{ color: "#00254f" }}>₹12,500</p>
                  <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-4 mb-4">
                <div className="product-card p-3">
                  <img src="https://via.placeholder.com/300x300" alt="Product 1" />
                  <h6 className="mt-3" style={{ color: "#00254f" }}>Pureit Copper+ Mineral RO+UV Water Purifier</h6>
                  <p className="text-muted" style={{ color: "#00254f" }}>₹15,000</p>
                  <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
                </div>
              </div>
              <div className="col-md-4 mb-4">
                <div className="product-card p-3">
                  <img src="https://via.placeholder.com/300x300" alt="Product 2" />
                  <h6 className="mt-3 " style={{ color: "#00254f" }}>Pureit Ultima Mineral RO+UV Water Purifier</h6>
                  <p className="text-muted" style={{ color: "#00254f" }}>₹18,000</p>
                  <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
                </div>
              </div>
              <div className="col-md-4 mb-4">
                <div className="product-card p-3">
                  <img src="https://via.placeholder.com/300x300" alt="Product 2" />
                  <h6 className="mt-3 " style={{ color: "#00254f" }}>Pureit Ultima Mineral RO+UV Water Purifier</h6>
                  <p className="text-muted" style={{ color: "#00254f" }}>₹18,000</p>
                  <button className="btn btn-primary bg-#00254f w-100" style={{ color: "#00254f" }}>Learn More</button>
                </div>
              </div>
              {/* <div className="col-md-4 mb-4">
          <div className="product-card p-3">
            <img src="https://via.placeholder.com/300x300" alt="Product 3"/>
            <h6 className="mt-3" style={{color: "#00254f"}}>Pureit Eco RO+MF Water  Water  Water  Water Purifier</h6>
            <p className="text-muted" style={{color: "#00254f"}}>₹12,500</p>
            <button className="btn border bg-red w-100" >Learn More</button>
          </div>
        </div> */}
            </div>

            {/* <!-- Add more rows for additional products --> */}
          </div>
        </div>
        </div>
      </div>

  
  );
}





