import React, { useState } from "react";

export default function Home() {
  // State to track the value of the range input
  const [price, setPrice] = useState(50000); // Default maximum price

  // List of products with names, images, and prices
  const products = [
    { 
      id: 1, 
      name: "Pureit Copper+ Mineral RO+UV Water Purifier", 
      price: 15000, 
      image: "https://tse4.mm.bing.net/th?id=OIP._k4ZlNcllPMJxvPwekpTcwHaHa&pid=Api&P=0&h=180" 
    },
    { 
      id: 2, 
      name: "Pureit Ultima Mineral RO+UV Water Purifier", 
      price: 18000, 
      image: "https://via.placeholder.com/300x300" 
    },
    { 
      id: 3, 
      name: "Pureit Eco RO+MF Water Purifier", 
      price: 12500, 
      image: "https://via.placeholder.com/300x300" 
    },
    { 
      id: 4, 
      name: "Pureit Advanced RO+UV Water Purifier", 
      price: 10000, 
      image: "https://via.placeholder.com/300x300" 
    },
    { 
      id: 5, 
      name: "Pureit Classic Mineral RO+MF Water Purifier", 
      price: 8000, 
      image: "https://via.placeholder.com/300x300" 
    },
    { 
      id: 6, 
      name: "Pureit Advanced Copper RO+UV Water Purifier", 
      price: 20000, 
      image: "https://via.placeholder.com/300x300" 
    }
  ];

  // Function to handle slider change
  const handleSliderChange = (event) => {
    setPrice(event.target.value);
  };

  // Filter products based on the price slider
  const filteredProducts = products.filter(product => product.price <= price);

  return (
    <div>
      {/* Carousel Section */}
      <div id="responsiveSlider" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">
          <div className="carousel-item active">
            <img src="https://via.placeholder.com/1200x500/007BFF/ffffff?text=Slide+1" className="w-100" alt="Welcome to Our Store" />
            <div className="slide-name">Welcome to Our Store</div>
          </div>
        </div>
      </div>
      
      {/* Filters Section */}
      <div className="container my-4">
        <div className="text-center mb-4">
          <h1 style={{ color: "#00254f" }}>Pureit Water Purifier</h1>
          <p style={{ color: "#00254f" }}>Choose a Water Purifier that best suits your needs & budget.</p>
        </div>

        <div className="row">
          <div className="col-md-3">
            <div className="filters mb-4">
              <h5>Filters</h5>
              <div className="mb-3">
                <label className="form-label">Budget</label>
                <input 
                  type="range" 
                  className="form-range" 
                  min="0" 
                  max="50000" 
                  value={price} 
                  onChange={handleSliderChange} 
                />
                <p>Selected Budget: <b>₹{price}</b></p>
              </div>
            </div>
          </div>
          
          {/* Products Section */}
          <div className="col-lg-9">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <div style={{ color: "#00254f" }}>
                <input type="checkbox" /> Recommendations based on Pin Code
              </div>
              <select className="form-select w-auto">
                <option>Price: High to Low</option>
                <option>Price: Low to High</option>
              </select>
            </div>

            <div className="row">
              {filteredProducts.map(product => (
                <div key={product.id} className="col-md-4 mb-4">
                  <div className="product-card p-3">
                    <img src={product.image} alt={product.name} className="w-100" />
                    <h6 className="mt-3" style={{ color: "#00254f" }}>{product.name}</h6>
                    <p className="text-muted" style={{ color: "#00254f" }}>₹{product.price}</p>
                    <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
                  </div>
                </div>
              ))}
              {filteredProducts.length === 0 && (
                <div className="col-12 text-center">
                  <p>No products available within this price range.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
} 








