import React, { useState } from 'react';

export default function Home() {
  // State to track the value of the range input and product list
  const [price, setPrice] = useState(0);
  
  const products = [
    { id: 1, name: 'Pureit Copper+ Mineral RO+UV Water Purifier', price: 15000, image: 'https://tse4.mm.bing.net/th?id=OIP._k4ZlNcllPMJxvPwekpTcwHaHa&pid=Api&P=0&h=180' },
    { id: 2, name: 'Pureit Ultima Mineral RO+UV Water Purifier', price: 18000, image: 'https://via.placeholder.com/300x300' },
    { id: 3, name: 'Pureit Eco RO+MF Water Purifier', price: 12500, image: 'https://via.placeholder.com/300x300' },
    { id: 4, name: 'Pureit Copper+ Mineral RO+UV Water Purifier', price: 15000, image: 'https://via.placeholder.com/300x300' },
    { id: 5, name: 'Pureit Ultima Mineral RO+UV Water Purifier', price: 18000, image: 'https://via.placeholder.com/300x300' },
    { id: 6, name: 'Pureit Eco RO+MF Water Purifier', price: 12500, image: 'https://via.placeholder.com/300x300' },
  ];

  // Function to handle slider change
  const handleSliderChange = (event) => {
    setPrice(event.target.value);
  };

  return (
    <div>
      <div id="responsiveSlider" className="carousel slide" data-bs-ride="carousel">
        {/* Carousel code remains the same */}
      </div>

      <div className="container my-4">
        <div className="text-center mb-4">
          <h1 style={{ color: "#00254f" }}>Pureit Water Purifier</h1>
          <p style={{ color: "#00254f" }}>Choose a Water Purifier that best suits your needs & budget.</p>
        </div>

        <div className="row">
          <div className="col-md-3">
            <div className="filters mb-4">
              <h5>Filters</h5>
              {/* Other filter options remain the same */}
              
              <div className="mb-3">
                <label className="form-label">Budget</label>
                <input
                  type="range"
                  className="form-range"
                  min="0"
                  max="50000"
                  value={price}
                  onChange={handleSliderChange}
                />
                <p>Selected Budget: <b>₹{price}</b></p>
              </div>
            </div>
          </div>

          <div className="col-lg-9">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <div style={{ color: "#00254f" }}>
                <input type="checkbox" /> Recommendations based on Pin Code
              </div>
              <select className="form-select w-auto">
                <option style={{ color: "#00254f" }}>Price: High to Low</option>
                <option style={{ color: "#00254f" }}>Price: Low to High</option>
              </select>
            </div>

            <div className="row">
              {products.filter(product => product.price <= price).map(product => (
                <div className="col-md-4 mb-4" key={product.id}>
                  <div className="product-card p-3">
                    <img src={product.image} alt={product.name} />
                    <h6 className="mt-3" style={{ color: "#00254f" }}>{product.name}</h6>
                    <p className="text-muted" style={{ color: "#00254f" }}>₹{product.price}</p>
                    <button className="btn btn-primary w-100" style={{ color: "#00254f" }}>Learn More</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}



